import React, {Component} from 'react';

class Cart extends Component {
	cartList = (key) => {
		const popsicle = this.props.popsicles[key];
		const inCartItem = this.props.inCart[key];

		if (!popsicle) return null;

		return (
			<li key={key} className="inCartItem">
				<h3 className="itemCount">{inCartItem}</h3>
				<h3 className="itemName">{popsicle.name}</h3>
				<h3 className="itemPrice">{(popsicle.price * inCartItem)}</h3>
			</li>
		);
	};

	render() {
		const cartTotal = Object.keys(this.props.inCart).reduce((accTotal, key) => {
			const popsicle = this.props.popsicles[key];
			const inCartItem = this.props.inCart[key];

			if (!popsicle) return accTotal + 0;
			return accTotal + popsicle.price * inCartItem;
		}, 0);

		return(
			<>
				<div id="cart">
					{Object.keys(this.props.inCart).map(key => this.cartList(key))}
					<h2 className="total">{cartTotal}</h2>
				</div>
			</>
		);
	}
}

export default Cart;
