import React, { Component } from 'react';
import Button from './Button';
import Sign from './Sign';
import signs from '../js/signs.js';
import Inventory from './Inventory';
import base from '../js/base';
import Cart from './Cart';

class App extends Component {
  state = {
    signs: [],
    inCart: []
  };

  componentDidMount(){
    this.ref = base.syncState('signs', {
      context: this,
      state: 'signs'
    });
  };

  loadSigns = () => {
    this.setState({ signs });
  };

  updateSign = (key, updatedSign) => {
    const signs = {...this.state.signs}
    signs[key] = updatedSign;
    this.setState({signs})
  };

  deleteSign = (key) => {
    const signs = {...this.state.signs}
    signs[key] = null;
    this.setState({signs});
  };

  addToCart = (key) => {
    console.log("added to cart");
    const inCart = {...this.state.inCart};

    if (inCart[key]) {
      inCart[key] += 1;
    } else {
      inCart[key] = 1;
    }
    this.setState({inCart});
  };

  deleteFromCart = (key) => {
    console.log("deleted from cart");
    const inCart = {...this.state.inCart};
    inCart[key] = null;
    this.setState({inCart});
  }

  increment = () => {
    console.log("increment");
  };

  decrement = () => {
    console.log("decrement");
  };

  render() {
    return (
      <>
        <div className="app">
          <Sign
            addToCart={this.addToCart} />

          <ul className="signs">
            {Object.keys(this.state.signs).map(key => (
             <Sign key={key} details={this.state.signs[key]} />
            ))}
          </ul>
          <section className="cart">
            <h2>Blah</h2> 
            <Cart 
              deleteFromCart={this.deleteFromCart}
              signs={this.state.signs}
              inCart={this.state.inCart}
            />
          </section>
          <section className="admin">
            <Button loadSigns={this.loadSigns} />
            <Inventory
              updateSign={this.updateSign}
              deleteSign={this.deleteSign}
              signs={this.state.signs}
            />
          </section>
        </div>
      </>
    );
  }
}

export default App;
